function reloadGrid(){var n=$("#StudentGrid").dxDataGrid("instance");n.refresh()}function showToast(n,t){$("#toast").dxToast("instance").option({type:n,message:t,visible:!0,displayTime:2e3})}function readresponse(n){if(n.data.success){var t=n.data.message;showToast("success",t)}else showToast("error",n.error.message)}function openStudentLibraryPopup(){$("#studentLibrary").dxPopup("instance").show()}function ClosePopUp(){$("#studentLibrary").dxPopup("instance").hide()}function sendData(){var n=document.getElementById("studentId").value,t=document.getElementById("bookId").value,i=document.getElementById("dateTime").value,r={StudentId:n,BookId:t,DateTime:i};$.ajax({url:"/Student/BookMenagment",type:"POST",data:r,success:function(n){var t=n.message;n.succes?showToast("success",t):showToast("error",t)},error:function(n){var t=n.responseJSON.message;showToast("error",t)}})}